INSERT INTO `products` (`id`, `name`, `price`, `discount`, `image`, `image_list`, `short_description`, `description`, `availability`, `information`, `view`, `outstanding`, `catalog_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(6, 'Wacom Intuos S (Black)', '2025.000', '0', 'product-1.png', '', '4096 mức cảm ứng áp lực, phiên bản không có bluetooth, kết nối Android được\r\nTặng bộ phần mềm bản quyền (Art / Photo / Comic)\r\nBảo hành 12 tháng, 1 đổi 1 trong 14 ngày đầu tiên', '<p>Khám phá những cách mới để sáng tạo với Wacom Intuos</p><p>Wacom Intuos giúp bạn tìm ra những cách mới để tạo. Được thiết kế cho những người lần đầu tiên vẽ, vẽ và chỉnh sửa ảnh bằng máy Mac hoặc PC, Wacom Intuos rất dễ cài đặt và sử dụng, đồng thời có ba ứng dụng sáng tạo độc đáo đi kèm để bạn bắt đầu *.</p><p>* Để nhận phần mềm miễn phí, bạn cần đăng nhập hoặc tạo ID Wacom và đăng ký Intuos của mình.</p><p>Tạo những gì bạn muốn, theo cách bạn muốn</p><p>Một chiếc bút máy tính bảng được thiết kế hoàn toàn dựa trên niềm đam mê của bạn, Wacom Intuos cung cấp cho bạn tất cả các công cụ cần thiết để nâng cao khả năng sáng tạo của bạn. Và tính linh hoạt và dễ sử dụng của nó có nghĩa là bạn có thể tùy chỉnh Wacom Intuos để phù hợp với cách làm việc của riêng bạn. Cho dù bạn thích vẽ nghệ thuật manga, vẽ màu nước kỹ thuật số hay chỉnh sửa ảnh của mình, bạn có thể điều chỉnh viên bút phù hợp với phong cách của bạn, theo cách của bạn.</p>', 100, '200 x 160 x 8.8mm|230g|Đen|2540 lpi', 0, 1, 1, '2021-08-05 21:43:26', '2021-08-07 04:23:26', NULL),
(7, 'Wacom Intuos S (Bluetooth, Black)', '2550.000', '20', 'product-2.png', '', '4096 mức cảm ứng áp lực, phiên bản có bluetooth, kết nối Android được\r\n\r\nTặng bộ phần mềm bản quyền (Art / Photo / Comic)\r\n\r\nBảo hành 12 tháng, 1 đổi 1 trong 14 ngày đầu tiên', '<p>Khám phá những cách mới để sáng tạo với Wacom Intuos</p><p>Wacom Intuos giúp bạn tìm ra những cách mới để tạo. Được thiết kế cho những người lần đầu tiên vẽ, vẽ và chỉnh sửa ảnh bằng máy Mac hoặc PC, Wacom Intuos rất dễ cài đặt và sử dụng, đồng thời có ba ứng dụng sáng tạo độc đáo đi kèm để bạn bắt đầu *.</p><p>* Để nhận phần mềm miễn phí, bạn cần đăng nhập hoặc tạo ID Wacom và đăng ký Intuos của mình.</p><p>Tạo những gì bạn muốn, theo cách bạn muốn</p><p>Một chiếc bút máy tính bảng được thiết kế hoàn toàn dựa trên niềm đam mê của bạn, Wacom Intuos cung cấp cho bạn tất cả các công cụ cần thiết để nâng cao khả năng sáng tạo của bạn. Và tính linh hoạt và dễ sử dụng của nó có nghĩa là bạn có thể tùy chỉnh Wacom Intuos để phù hợp với cách làm việc của riêng bạn. Cho dù bạn thích vẽ nghệ thuật manga, vẽ màu nước kỹ thuật số hay chỉnh sửa ảnh của mình, bạn có thể điều chỉnh viên bút phù hợp với phong cách của bạn, theo cách của bạn.</p>', 80, '200 x 160 x 8.8mm|250g|Đen|2540 lpi', 0, 0, 1, '2021-08-05 21:45:31', '2021-08-07 04:29:09', NULL),
